                .restEndpoint(data.confluent_kafka_cluster().destination().rest_endpoint())
                .credentials(KafkaMirrorTopicKafkaClusterCredentialsArgs.builder()
                    .key(confluent_api_key.app-manager-destination-cluster-api-key().id())
