	"github.com/pulumi/pulumi/sdk/v3/go/pulumi"
)

func main() {
	pulumi.Run(func(ctx *pulumi.Context) error {
		main, err := confluentcloud.GetUsers(ctx, nil, nil)
		if err != nil {
			return err
		}
		ctx.Export("users", main.Ids)
		return nil
	})
}
